import { useEffect, useState } from "react";
import { ethers } from "ethers";

// Components
import Navigation from "./components/Navigation";
import Section from "./components/Section";
import Product from "./components/Product";

// ABIs
import Dappazon from "./abis/Dappazon.json";

// Config
import config from "./config.json";

function App() {
  const [account, setAccount] = useState(null);
  const [dappazon, setDappazon] = useState(null);
  const [provider, setProvider] = useState(null);

  const loadBlockchainData = async () => {
    // Connect to blockchain
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    setProvider(provider);
    const network = await provider.getNetwork();
    console.log(network); // prints - {chainId: 31337, name: 'unknown'}

    // Connect to smart contracts (create JS versions so can call functions)
    const dappazon = new ethers.Contract(
      config[network.chainId].dappazon.address, // so just use network.chainId to access 31337
      Dappazon,
      provider
    );
    setDappazon(dappazon);

    // Load products
    const items = [];
    for (var i = 0; i < 9; i++) {
      const item = await dappazon.items(i);
      items.push(item);
    }
    const electronics = items.filter((item) => item.category === "electronics");
    console.log("items");
    console.log(items);
    //console.log(electronics);
  };

  useEffect(() => {
    loadBlockchainData();
  }, []);

  return (
    <div>
      <Navigation account={account} setAccount={setAccount} />
      <h2>Dappazon Best Sellers</h2>
    </div>
  );
}

export default App;
